// Build JobRow from Sheets row
const row = items[0].json;

return [{
  json: {
    job_id: row.job_id || '',
    company: row.company || '',
    company_slug: row.company_slug || '',
    domain: row.domain || '',
    job_title: row.job_title || '',
    job_family: row.job_family || '',
    job_location: row.job_location || '',
    job_url: row.job_url || '',
    jd_text: row.jd_text || '',
    jd_keywords: row.jd_keywords || '',
    status: row.status || 'ready',
    resume_status: row.resume_status || 'pending',
    cover_letter_status: row.cover_letter_status || 'pending',
    notes: row.notes || '',
    last_synced_at: row.last_synced_at || new Date().toISOString()
  }
}];

// Extract JD Insights (simplified version of extractJdInsights)
const job = items[0].json;
const jdText = job.jd_text || '';

// Simple keyword extraction
function getKeywords(text) {
  if (!text) return [];
  const words = text
    .toLowerCase()
    .replace(/[^a-z0-9+\s]/g, ' ')
    .split(/\s+/)
    .filter(w => w.length > 3 && w.length < 25);
  const freq = {};
  words.forEach(w => { freq[w] = (freq[w] || 0) + 1; });
  return Object.entries(freq)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 10)
    .map(([word]) => word);
}

function extractPhrases(text, pattern) {
  const matches = [];
  const regex = new RegExp(pattern, 'gi');
  let m;
  while ((m = regex.exec(text)) !== null) {
    matches.push(m[1].trim());
  }
  return matches;
}

const keywordList = getKeywords(jdText);
const requirements = extractPhrases(jdText, /(?:responsibilities|requirements|must have|you will|you should):?\s*([\s\S]*?)(?:\n\n|$)/);
const niceToHave = extractPhrases(jdText, /(?:nice to have|preferred|bonus):?\s*([\s\S]*?)(?:\n\n|$)/);

return [{
  json: {
    ...job,
    jd_insights: {
      jd_keywords: keywordList.slice(0, 10),
      top_requirements: requirements.length ? requirements[0].split(/\n|•|-/).map(s => s.trim()).filter(Boolean).slice(0, 5) : [],
      nice_to_have: niceToHave.length ? niceToHave[0].split(/\n|•|-/).map(s => s.trim()).filter(Boolean).slice(0, 5) : []
    }
  }
}];

// Build filesystem paths
const job = items[0].json;
const path = require('path');
const cwd = process.cwd();

return [{
  json: {
    ...job,
    jobFolder: path.join(cwd, 'outputs', job.company_slug, job.job_id),
    resumePath: path.join(cwd, 'outputs', job.company_slug, job.job_id, 'resume.tex'),
    coverLetterPath: path.join(cwd, 'outputs', job.company_slug, job.job_id, 'cover_letter.tex')
  }
}];
